templates.template:
 	public static function catalogo_numcom($$objhtml)
	{
		$$sql="and  ( a.codcta like '%V_0%' and b.descta like '%V_1%') order by a.codcta";

		$$catalogo = array(
		    $$sql,
		    array('== campo1 ==','== campo2 =='),
		    array($$objhtml),
		    array('== campo de la vista =='),
		    100
		    );

	    return $$catalogo;
	}
